query GetFriends($page: Int, $size: Int, $searchQuery: String) {
  user {
    id
    friends(page: $page, size: $size, searchQuery: $searchQuery) {
      edges {
        node {
          id
          friends(page: $page, size: $size, searchQuery: $searchQuery) {
            edges {
              node {
                id
              }
              __typename
            }
            pageInfo {
              hasPreviousPage
              hasNextPage
              __typename
            }
            __typename
          }
        }
        __typename
      }
      pageInfo {
        hasPreviousPage
        hasNextPage
        __typename
      }
      __typename
    }
    __typename
  }
}
